      var deductibleKey = 'deductible' + (isFamilyPlan && famSuffix || indSuffix);
      var deductibleRxKey = 'deductiblerx' + (isFamilyPlan && famSuffix || indSuffix);
      var oopMaxKey = 'oopmax' + (isFamilyPlan && famSuffix || indSuffix);
